import React, { Fragment } from "react";
import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  getAll,
  getTemperaments,
  filterByTemperaments,
  filterCreated,
  orderByName,
  getBreeds,
  filterBreeds,
  orderByWeight,
  clean,
} from "../actions";
import Card from "./Card";
import SearchBar from "./SearchBar";
import Paginado from "./Paginado";
import { Link } from "react-router-dom";
import "./Home.css";

export default function Home() {
  const dispatch = useDispatch();
  const allDogs = useSelector((state) => state.dogs); //trae todo lo que este en el reducer de dogs
  const allTemperaments = useSelector((state) => state.temperament);
  const allBreeds = useSelector((state) => state.breeds);
  const [, /*orden*/ setOrden] = useState("");
  const [currentPage, setCurrentPage] = useState(1); //seteado el estado local, la pag actual y el estado que setee la pag
  const [dogsPerPage /*setDogsPerPage*/] = useState(8); //los perros por paginas
  const indexOfLastDog = currentPage * dogsPerPage; //Mi paginas por los dogs por pag
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = allDogs?.slice(indexOfFirstDog, indexOfLastDog); //divide el array de perros para cada pag, dejando 8porpag
  const [, /*[order*/ setOrder] = useState("");

  const paginado = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  useEffect(() => {
    //nos traemos del estado los perro cuando el componente se monta
    dispatch(getAll()); //es lo mimso que el mapdistpachtoprops
    dispatch(getTemperaments());
    dispatch(getBreeds());
  }, [dispatch]);

  function handleClick(e) {
    e.preventDefault();  //esta funcion se va a disparar en el select, cuando modifique algo
    dispatch(getAll()); // por si se bugeea
  }
  function handleFilterBreeds(e) {
    dispatch(filterBreeds(e.target.value));  // el target.value es el valor del select que estoy eligiendo
    dispatch(clean());                    // y despacha la accion con ese payload
    setCurrentPage(1);
    setOrder(`${e.target.value}`);
  }

  function handleFilterTemperaments(e) {
    dispatch(filterByTemperaments(e.target.value));
    dispatch(clean());
    setCurrentPage(1);
    setOrder(`${e.target.value}`);
  }

  function handleFilterCreated(e) {
    if (e.length === 0) {
      alert("no hay perro para mostrar");
    } else {
      dispatch(filterCreated(e.target.value));
      setCurrentPage(1);
      setOrder(`${e.target.value}`);
    }
  }

  function handleSort(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value));
    setCurrentPage(1);
    setOrder(`${e.target.value}`);
  }

  function handleByWeight(e) {
    e.preventDefault();
    dispatch(orderByWeight(e.target.value));
    setCurrentPage(1);
    setOrder(`${e.target.value}`);
  }

  return (
    <div>
      <div>
        <div>
          <div className="dog"> 
            <Link to="/">
              <div className="dog-head">
                <div className="dog-ears ears-left"></div>
                <div className="dog-ears ears-right"></div>
                <div className="dog-eyes"></div>
                <div className="dog-mouth">
                  <div className="dog-nose"></div>
                  <div className="dog-tongue"></div>
                </div>
              </div>
              <div className="dog-tail"></div>
              <div className="dog-body">
                <div className="dog-foot"></div>
              </div>
            </Link>
          </div>
<div><h3><u>Pi Tomas Tinto</u></h3></div>
          <div className="icons-wrapper">
            <Fragment>
              <a
                href="https://www.instagram.com/tomas_tinto/"
                target="_blank"
                className="ri-instagram-line icon"
                rel="noreferrer"
              >
                <img
                  src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Instagram_icon.png"
                  alt="img not found"
                  width="40px"
                  height="45px"
                />
              </a>

              <a
                href="https://www.facebook.com/tititinto/"
                target="_blank"
                className="ri-facebook-circle-line icon"
                rel="noreferrer"
              >
                <img
                  src="data:image/png;base64,"
                  alt="img not found"
                  width="40px"
                  height="45px"
                />
              </a>

              <a
                href="https://web.whatsapp.com/"
                target="_blank"
                className="ri-whatsapp-line icon"
                rel="noreferrer"
              >
                <img
                  src="https://png.pngtree.com/element_our/md/20180626/md_5b321c98efaa6.jpg"
                  alt="img not found"
                  width="40px"
                  height="45px"
                />
              </a>

              <a
                href="https://github.com/TomasTinto1234"
                className="ri-instagram-line icon"
                target="_blank"
                rel="noreferrer"
              >
                <img
                  src="https://cdn-icons-png.flaticon.com/512/25/25231.png"
                  alt="img not found"
                  width="40px"
                  height="45px"
                />
              </a>

              <a
                href="https://www.linkedin.com/in/tomas-tinto-320a85236/"
                className="ri-facebook-circle-line icon"
                target="_blank"
                rel="noreferrer"
              >
                <img
                  src="data:image/png;base64,"
                  alt="img not found"
                  width="40px"
                  height="45px"
                />
              </a>
            </Fragment>
          </div>
          <div className="ball"></div>
        </div>
        <SearchBar/>
        <div>
          <div className="columns">
            <Link to="/dogs">
              <button className="select">CREA UN PERRO</button>
            </Link>
          </div>

          <div>
            <div className="columns">
              <select className="select" onChange={(e) => handleSort(e)}>
                <option hidden={true}>Orden mayor a menor </option>
                <option value="asc">A - Z</option>
                <option value="desc">Z - A</option>
              </select>
              <select className="select" onChange={(e) => handleByWeight(e)}>
                <option hidden={true}>Orden Por Peso</option>
                <option value="weight asc">menor Peso</option>
                <option value="weight ">mayor Peso</option>
              </select>
            </div>
            <div className="columns">
              <select
                className="select"
                onChange={(e) => handleFilterCreated(e)}
              >
                <option hidden={true}>Origen</option>
                <option value="all">Todas Las Razas</option>
                <option value="api">Razas Existentes</option>
                <option value="createdInDb">
                  Razas Creadas En Base de Datos
                </option>
              </select>
              <select
                className="select"
                name="razas"
                id="breeds"
                onChange={(e) => handleFilterBreeds(e)}
              >
                <option hidden={true} value="allRazas">
                  Razas
                </option>
                {allBreeds &&
                  allBreeds.map((breed, r) => {
                    return (
                      <option key={r} value={breed}>
                        {breed}
                      </option>
                    );
                  })}
              </select>
<div>
              <select 
                className="select"
                onChange={(e) => handleFilterTemperaments(e)}
              >
                <option key={0} value="Alltemp">
                  Temperamentos
                </option>
                {allTemperaments &&
                  allTemperaments
                    ?.sort(function (a, b) {
                      if (a.name < b.name) return -1;
                      if (a.name > b.name) return 1;
                      return 0;
                    })
                    .map((e, g) => {
                      return (
                        <option key={g} value={e.name}>
                          {e.name}
                        </option>
                      );
                    })}
              </select>
            </div>
                    </div>

            <button
              className="select"
              onClick={(e) => {
                handleClick(e);
              }}
            >
              RECARGAR PERROS
            </button>
            <Paginado // aca le agrego las props que necesita el paginado para funcionar
              dogsPerPage={dogsPerPage}
              allDogs={allDogs.length}
              paginado={paginado}
            />
          </div>
        </div>
      </div>

      <div className="container">
        {currentDogs.length === 0 ? ( //si no hay perros que me muestre esta imagen
          <div>
            <h1>NO HAY PERROS PARA MOSTRAR</h1>
            <img
              src="https://i.pinimg.com/originals/79/a4/b9/79a4b912377bcf73101684d62ea9590f.gif"
              alt="img not found"
              width="400px"
              height="450px"
            />
          </div>
        ) : (
          currentDogs &&
          currentDogs?.map((e, f) => {
            return (
              <div className="caja" key={f}>
                <Link to={"/detail/" + e.id} key={f.id}>
                  <Card
                    id={e.id}
                    name={e.name}
                    image={e.image}
                    weightMin={e.weightMin}
                    weightMax={e.weightMax}
                    heightMin={e.heightMin}
                    heightMax={e.heightMax}
                    breeds={e.breeds}
                    temperament={e.temperament}
                    life_span={e.life_span}
                  />
                </Link>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
